import React,{useEffect,useState} from 'react'

import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import axios from "axios";
import { Link } from 'react-router-dom';
import { Button } from 'primereact/button';
const Guy = () => {
    const [loading1, setLoading1] = useState(true);


    const [products, setProducts] = useState([]);
    useEffect(() => {
        const getDatz=async ()=>{
            const statz = await axios.get('https://api.verxid.site/mtn/viewAllSim');
            console.log(statz.data.mtn)

        setProducts(statz.data.mtn)
        setLoading1(false)
        }
                getDatz()
    }, []); // eslint-disable-line react-hooks/exhaustive-deps



    const statusBodyTemplate2 = (rowData) => {
        return <Link  className={`btn btn-primary font-bold`} to={`/single-sim/${rowData.id}`} >
VIEW DEVICE
        </Link>
    }


  return (
   <div className='grid'>
 <div className="col-12">
                <div className="card border-round shadow-2 ">
                <div className="mb-3 flex align-items-center justify-content-between p-3">
        <span className="text-xl font-medium text-900">All Devices</span>
        <Button label="New" icon="pi pi-plus" />
    </div>
             <DataTable value={products} 

                    loading={loading1}
                     responsiveLayout="stick"
                    scrollable
                     paginator
                     paginatorTemplate="CurrentPageReport FirstPageLink PrevPageLink PageLinks NextPageLink LastPageLink RowsPerPageDropdown"
                     currentPageReportTemplate="Showing {first} to {last} of {totalRecords}" rows={5} rowsPerPageOptions={[5,10,50]}

                        >

                        <Column field="nin" header="NIN"></Column>
                            <Column field="msisdn" header="msisdn"></Column>
                            <Column field="sim_serial" header="sim_serial"></Column>
                            <Column field="alt_phone" header="Alt-phone"></Column>
                            {/* <Column field="mother_maiden" header="Mother's Name" sortable></Column>
                            <Column field="nin" header="NIN"></Column> */}
                           {/* <Column field="email" header="Email"></Column>
                           <Column field="place_birth" header="Place Of Birth"></Column>
                           <Column field="agentemail" header="Agent Email"></Column> */}
                        <Column field="id" header="Action" body={statusBodyTemplate2} />
                    </DataTable>
                </div>
            </div>

</div> 
  )
}

export default Guy
